import React, { Component } from 'react'
import Weath from './components/Weath'
import Form from './components/Form'
let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  constructor(props){
    super(props)
    this.state={
      city: undefined
    }
  this.qetWeather = this.qetWeather.bind(this)
  }
  qetWeather = async(e)=>{
    e.preventDefault()
    let city = e.target.elements.city.value
    let apiUrl = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${ApiKey}`)
    let api = apiUrl.json()
    console.log(api)
    this.setState({
      city: {
        "coord": {
            "lon": -0.1257,
            "lat": 51.5085
        },
        "weather": [
            {
                "id": 802,
                "main": "Clouds",
                "description": "scattered clouds",
                "icon": "03d"
            }
        ],
        "base": "stations",
        "main": {
            "temp": 296.4,
            "feels_like": 296.01,
            "temp_min": 294.64,
            "temp_max": 297.76,
            "pressure": 1017,
            "humidity": 47
        },
        "visibility": 10000,
        "wind": {
            "speed": 1.03,
            "deg": 0
        },
        "clouds": {
            "all": 44
        },
        "dt": 1661532901,
        "sys": {
            "type": 2,
            "id": 2075535,
            "country": "GB",
            "sunrise": 1661490207,
            "sunset": 1661540503
        },
        "timezone": 3600,
        "id": 2643743,
        "name": "London",
        "cod": 200
    }
    })

  }
  render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>
        <Form city={this.state.city}/>
      </div>
    )
  }
}

export default App
