import React, { Component } from 'react'

export class Form extends Component {
  App = ()=>{
    return(<div>
      {this.props.pressure>1013?'Давление повышенное':'Давление пониженное'}
    </div>
    )
  }
  render() {
  return (
      <div>
        {this.props.cityName&&
        <div>
          <p>{this.props.cityName+' , '+ this.props.country}</p>
          <p>{Math.floor(this.props.temp-273.15)+ '°C'}</p>
          <p>{Math.floor(this.props.wind)}</p>
          <p>{this.props.sky}</p>
          <p>{this.props.pressure}</p>
          <p>{this.props.error}</p>
          <p>{this.App()}</p>
        </div>
        }
        
      </div>
    )
  }
}

export default Form
