import React, { Component } from 'react'
import Weath from './components/Weath'
import Form from './components/Form'
let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  state={
      city: undefined
  }
  qetWeather = async(e)=>{
    e.preventDefault()
    let city = e.target.elements.city.value
    let apiUrl = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${ApiKey}`)
    let api = apiUrl.json()
    console.log(api)
    this.setState({
      city: api.PromiseResult.name
    })

  }
  render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>
        <Form city={this.state.city}/>
        <div>this.api</div>
      </div>
    )
  }
}

export default App
