import React, { Component } from 'react'
import Weath from './components/Weath'
let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  constructor(props){
    super(props)
    this.state={
      items: [{
        city: ''
      }]
    }
  this.qetWeather = this.qetWeather.bind(this)
  }
  qetWeather = async(e)=>{
    e.preventDefault()
    let city = e.target.elements.city.value
    let apiUrl = await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${ApiKey}`)
    let api = apiUrl.json()
    console.log(api)
    this.setState({
      items:[...this.state.items, api.PromiseResult.name]
    })
}
  render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>
        <div>{this.state.items}</div>
      </div>
    )
  }
}

export default App
