import React, { Component } from 'react'
import Weath from './components/Weath'

let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  state={
    cit: undefined
  }
  qetWeather = async(e)=>{
    e.preventDefault()
    let city = e.target.elements.city.value
    let apiUrl = await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${ApiKey}`)
    let api = apiUrl.json()
    console.log(api)
    this.setState({cit: api.name})
    console.log(this.state.cit)
    }
  render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>

      </div>
    )
  }
}

export default App
