import React,{useState} from 'react'

export default function Weath(props){
  let [value, setValue] = useState(value)
  return (
      <div>
        <form className='weathForm1' onSubmit={props.qetWeather}>
        <div className='weathDiv1'>Узнайте погоду в вашем городе</div>
        <input name='city' value={value} onChange={()=>{setValue(value)}} className='weathInp1' placeholder='введите в каком городе' /><br></br>
        <button className='weathBt1'>Узнать погоду {'в ' + value}</button>
        </form>
      </div>
    )
}
