import React, { Component } from 'react'
import Weath from './components/Weath'
let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  qetWeather = async(e)=>{
    e.preventDefault()
    let city = e.target.elements.city.value
    let apiUrl = await fetch(`api.openweathermap.org/data/2.5/weather?q=${city}&appid=${ApiKey}&units=metric`)
    let api = apiUrl.json()
    console.log(api)
}
  render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>
      </div>
    )
  }
}

export default App
