import React, { Component } from 'react'
import Weath from './components/Weath'
import Form from './components/Form'
let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  constructor(props){
    super(props)
    this.state={
      items:'',
      name: ''
    }
  }
  qetWeather = async(e)=>{
    e.preventDefault()
    let city = e.target.elements.city.value
    await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${ApiKey}`)
    .then(res=>res.json())
    .then(
      (result)=>{
        this.setState({
          items: result.name
        })
      console.log(result)
      }
    )
    }
  render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>
        <Form cityName={this.state.items}/>
      </div>
    )
  }
}

export default App
