import React, { Component } from 'react'

export class Form extends Component {
  Pressure (){
    if(this.props.pressure<1013){
      <div>Давление повышенное</div>
    }else if(this.props.pressure===1013){
      <div>Идеальное давление</div>
    }else{
      <div>Давление пониженное</div>
    }
    }
  render() {
    return (
      <div>
        <p>{this.props.cityName+' , '+ this.props.country}</p>
        <p>{Math.floor(this.props.temp-273.15)+ '°C'}</p>
        <p>{Math.floor(this.props.wind)}</p>
        <p>{this.props.sky}</p>
        <p>{this.props.pressure}</p>
        <div>{this.Pressure}</div>
      </div>
    )
  }
}

export default Form
