import React, { Component } from 'react'

export class Map extends Component {
    render() {
    let API_KEY = 'd64dc302-7d89-4b8b-8f21-ff443fb18863'
        fetch(`https://api-maps.yandex.ru/2.1/?lang=ru_RU&amp;apikey=${API_KEY}`)
        myMap.ready(init); 
	var myMap;
	
	function init() {
	
		myMap = new myMap.Map("map", {
			center: [43.238253, 76.945465], // Координаты центра карты
			zoom: 13 // Маштаб карты
		}); 
	
		myMap.controls.add(
			new myMap.control.ZoomControl()  // Добавление элемента управления картой
		); 
	
		myPlacemark = new myMap.Placemark([43.238253,76.945465], { // Координаты метки объекта
			balloonContent: "<div class='ya_map'>Заезжайте в гости!</div>" // Подсказка метки
		}, {
			preset: "twirl#redDotIcon" // Тип метки
		});
		
		myMap.geoObjects.add(myPlacemark); // Добавление метки
		myPlacemark.balloon.open(); // Открытие подсказки метки
		
	};
    return (
      <div>
        <div id='map'></div>
      </div>
    )
  }
}

export default Map
