import React, { Component } from 'react'
import Weath from './components/Weath'
import Form from './components/Form'
let ApiKey = '9713ebee0d0cc5bb917e3207c81b045c'
export class App extends Component {
  constructor(props){
    super(props)
    this.state={
      country: '',
      city: '',
      temp: '',
      pressure: ''
    }
  }
  qetWeather = async(e)=>{
    e.preventDefault()
    if(this.state.cityName){
      let city = e.target.elements.city.value
      await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${city}&APPID=${ApiKey}`)
      .then(res=>res.json())
      .then(
        (result)=>{
          this.setState({
            country: result.sys.country,
            city: result.name,
            temp: result.main.temp,
            wind: result.wind.speed,
            sky: result.weather[0].description,
            pressure: result.main.pressure
          })
        console.log(result)
        }
      )
      }else{<div>Введите город</div>}
    }
   render() {
    return (
      <div>
        <Weath qetWeather={this.qetWeather}/>
        <Form 
        country = {this.state.country}
        cityName={this.state.city}
        temp={this.state.temp}
        wind={this.state.wind}
        sky={this.state.sky}
        pressure={this.state.pressure}
        />

      </div>
    )
  }
}

export default App
